mapper.select:
		
	Select * from (
		Select a.*, ROWNUM AS rnum from (
			Select * from BOARD order by ref DESC, re_level ASC
			where del_flag='N' and board_type = #{board_type}
			)a) 
	where rnum between ${} and ${}

mapper.update:
Update board 
set title = #{title}
, board_type = #{board_type}
, content = #{content}
, modify_id = #{modify_id}
, modify_date = sysdate
where idx = #{idx}

Update board 
set del_flag = 'Y'
where idx = #{idx}
